import React from 'react';
import { Link } from 'react-router-dom';
import './CategoryCircles.css';

const CategoryCircles = ({ categories }) => {
  const getImageUrl = (url) => {
    if (!url) return 'https://placehold.co/100?text=Category';
    if (url.startsWith('http')) return url;
    return `http://localhost:5000${url}`;
  };

  // Only show top-level categories (no parent) or first 10
  const displayCategories = categories.filter(c => !c.parentCategory).slice(0, 10);

  if (!displayCategories.length) return null;

  return (
    <div className="category-circles-section">
      <h3 className="section-header">Shop by Category</h3>
      <div className="category-circles-container">
        {displayCategories.map(category => (
          <Link key={category._id} to={`/products?category=${category._id}`} className="category-circle-item">
            <div className="circle-image-wrapper">
              <img 
                src={getImageUrl(category.image?.url)} 
                alt={category.name} 
                className="circle-image"
              />
            </div>
            <span className="circle-name">{category.name}</span>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default CategoryCircles;
